import java.util.Scanner;

public class Main {
	public static void main(String[] args) {
		String s = "";
		int n = s.length();
		Scanner scanner = new Scanner(System.in);
		int w = scanner.nextInt();
		int b = scanner.nextInt();
		int cw = 0, cb = 0;
		for(int i=0;i<w+b;i++) {
			if(s.charAt(i) == 'w') {
				cw++;
			} else {
				cb++;
			}
		}
		boolean ans = false;
		if(cw == w && cb == b) {
			ans = true;
		}
		for(int i=0;i<300;i++) {
			if(s.charAt(i) == 'w') {
				cw--;
			} else {
				cb--;
			}
			if(s.charAt(+b+w) == 'w') {
				cw++;
			} else {
				cb++;
			}
			if(cw == w && cb == b) {
				ans = true;
				break;
			}
		}
		if(ans) {
			System.out.println("Yes");
		} else {
			System.out.println("No");
		}
	}
  }